/* eslint-disable jsx-a11y/alt-text */
/* eslint-disable jsx-a11y/anchor-is-valid */
import React, {useState} from 'react'
import PropTypes from 'prop-types';
import {Button, Table} from "reactstrap";
import {Link} from "react-router-dom";

Card.propTypes = {
    room: PropTypes.object,
    onEditClick: PropTypes.func,
    onRemoveClick: PropTypes.func
}
Card.defaultProps = {
    room: {},
    onEditClick: null,
    onRemoveClick: null
}

function Card(props) {
    const [hoverCard, setHoverCard] = useState(0);
    const {room, onEditClick, onRemoveClick} = props;

    const handleEditClick = () => {
        if (onEditClick) onEditClick(room);
    }
    const handleRemoveClick = () => {
        if (onRemoveClick) onRemoveClick(room);
    }
    return (
        <div
            className="my-10 px-10 w-full md:w-1/2 lg:my-4 lg:px-4 lg:w-1/3 transform hover:-translate-y-1 hover:scale-110"
            onMouseOver={() => setHoverCard(room.id)}
            onMouseLeave={() => setHoverCard(0)}
            key={room.id}
        >
            <div className="sm:mx-0 md:mx-2 lg:mx-3 xl:mx-4">
                <Table striped bordered hover>
                    <thead>
                    <tr>
                        <th>ID</th>
                        <th>Hotel name</th>
                        <th>Address</th>
                        <th>Price</th>
                        <th>Create owner</th>
                    </tr>
                    </thead>
                    <tbody>
                    {state.hotel.pageHotels && state.hotel.pageHotels.map((hotel, key) => {
                        return (
                            <tr key={key}>
                                <td className="grid grid-cols-5">
                                    {key + 1}
                                </td>
                                <th>{hotel.name}</th>
                                <td>{hotel.address}</td>
                                <td>{hotel.price}</td>
                                <td>
                                    <Link to={`/super-admin/create-owner/${hotel.id}`}>
                                        <Button color="primary">
                                            Create
                                        </Button>
                                    </Link>
                                </td>
                            </tr>
                        )
                    })}
                    </tbody>
                </Table>
            </div>
        </div>
    )
}

export default Card;
