import axios from "axios";
import {
    GET_HOTEL_IMAGES,
    GET_
} from "../actionTypes";

const url = 'https://booking-nestjs.herokuapp.com';
//-----------------------------------------
export const getHotelImages = (dispatch) => {
    axios(`${url}/hotels/images`)
        .then((response) => {
            dispatch({
                type: GET_HOTEL_IMAGES,
                payload: response.data.data
            });
        })
        .catch((error) => {});
};
//-----------------------------------------
export const getFeaturedHotels = (dispatch) => {
    axios(`${url}/api/hotels`)
        .then((response) => {
            dispatch({
                type: GET_FEATURED_HOTELS,
                payload: response.data.data
            });
        })
        .catch((error) => {});
};
