import React, {useEffect} from "react";

export const Loading = () => {
    useEffect(() => {
        document.title = `Loading...`
    })
    return (
        <div className="fixed top-0 right-0 h-screen w-screen z-50 flex justify-center items-center opacity-50 bg-gray-400">
            <div className="animate-spin rounded-full h-40 w-40 border-t-3 border-b-3 border-gray-900"></div>
            <h2 class="text-center text-white text-xl font-semibold">Loading...</h2>
            <p class="w-1/3 text-center text-white">This may take a few seconds, please don't close this page.</p>
        </div>
    );
}
