import React, {useEffect, useState} from "react";
import {Modal, ModalFooter, ModalBody, ModalHeader, FormGroup, Spinner, Button} from "reactstrap"
import PropTypes from "prop-types";
import {Formik, FastField, Form} from "formik";
import InputField from "../../../../custom-fields/InputField";
import Title from "../../../Global/Title";
import SelectField from "../../../../custom-fields/SelectField";
import {CITY_OPTIONS} from "../../../../constants/global";
import {useSelector} from "react-redux";
import {useHistory} from "react-router-dom";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faPlusCircle} from "@fortawesome/free-solid-svg-icons";
import * as Yup from "yup";

AddRoomForm.defaultProps = {
    onSubmit: null,
}

AddRoomForm.propTypes = {
    onSubmit: PropTypes.func.isRequired,
}

function AddRoomForm(props) {
    const initialValues = {
        price: '',
        name: '',
        capacity: '',
        description: '',
    }
    const state = useSelector((state) => state);

    const [show, setShow] = useState(false);
    const toggleShow = () => setShow(!show);

    const history = useHistory();
    useEffect(() => {
        !state.auth.isAuthenticated && history.push("/login");
    },[]);

    const validationSchema = Yup.object().shape({
        name: Yup.string().required('Name is required').nullable(),
        price: Yup.string().required('Price is required').nullable(),
        capacity: Yup.string().required('Max guests is required').nullable(),
    })


    return (
        <Formik
            initialValues={initialValues}
            validationSchema={validationSchema}
            onSubmit={props.onSubmit}
        >
            {formikProps => {
                const {values, isSubmitting} = formikProps;
                console.log(values);
                return (
                    <div className="flex justify-center items-center">
                        <FontAwesomeIcon
                            icon={faPlusCircle}
                            style={{
                                width: "50%",
                                height: "50%",
                                cursor: "pointer",
                                margin: "10px"
                            }}
                            onClick={toggleShow}
                        />
                        <Modal
                            size="lg"
                            aria-labelledby="contained-modal-title-vcenter"
                            centered
                            isOpen={show} toggle={toggleShow}
                        >
                            <ModalHeader>
                                <Title title="Create a new room"/>
                            </ModalHeader>
                            <ModalBody>
                                <div className="w-full xl:px-20 lg:px-16 md:px-3 sm:px-0 items-center bg-white">
                                    <div className="w-full xl:px-4 lg:px-5 md:px-1 items-center bg-white">
                                        <div className="container mx-auto">
                                            <Form className="max-w-full mx-auto bg-gray-200 p-16 rounded-md shadow-sm">
                                                <div className="flex flex-col w-full">
                                                    <FastField
                                                        name="name"
                                                        component={InputField}
                                                        placeholder="Name"
                                                    />
                                                    <FastField
                                                        name="price"
                                                        component={InputField}
                                                        placeholder="Price"
                                                    />
                                                    <FastField
                                                        name="phoneNumber"
                                                        component={InputField}
                                                        placeholder="Phone Number"
                                                    />
                                                    <FastField
                                                        name="cityId"
                                                        component={SelectField}
                                                        placeholder="City"
                                                        options={CITY_OPTIONS}
                                                    />
                                                    <FormGroup>
                                                        <Button
                                                            type="submit"
                                                            className="w-full"
                                                            color="primary"
                                                            onClick={toggleShow}
                                                        >
                                                            {isSubmitting && <Spinner size="sm"/>}
                                                            Create
                                                        </Button>
                                                    </FormGroup>
                                                </div>
                                            </Form>
                                        </div>
                                    </div>
                                </div>
                            </ModalBody>
                            <ModalFooter>
                                <Button onClick={toggleShow}>Close</Button>
                            </ModalFooter>
                        </Modal>
                    </div>
                )
            }}
        </Formik>
    );
}

export default AddRoomForm;
