import React, {useState} from "react";
import {FormGroup, Spinner, Button, Modal, ModalBody, ModalFooter} from "reactstrap"
import PropTypes from "prop-types";
import {Formik, FastField, Form} from "formik";
import InputField from "../../../../custom-fields/InputField";
import Title from "../../../Global/Title";
import SelectField from "../../../../custom-fields/SelectField";
import {GUEST_OPTIONS} from "../../../../constants/global";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faPlusCircle} from "@fortawesome/free-solid-svg-icons";
import {Link} from "react-router-dom";

RoomEditForm.defaultProps = {
    onSubmit: null,
    room: null,
}

RoomEditForm.propTypes = {
    onSubmit: PropTypes.func.isRequired,
    room: PropTypes.object.isRequired,
}

function RoomEditForm(props) {
    const initialValues = {
        price: '',
        name: '',
        capacity: '',
        description: '',
    }
    const [show, setShow] = useState(false);
    const toggleShow = () => setShow(!show);


    return (
        <Formik
            initialValues={initialValues}
            onSubmit={props.onSubmit}
        >
            {formikProps => {
                const {values, isSubmitting} = formikProps;
                console.log(values);
                return (
                    <div>
                        <Link to={`/admin/room/${room.id}`}>
                            <Button color="primary">
                                Edit
                            </Button>
                        </Link>
                        <Modal
                            size="lg"
                            aria-labelledby="contained-modal-title-vcenter"
                            centered
                            isOpen={show} toggle={toggleShow}
                        >
                            <Title title="Create a new room" className="w-full"/>
                            <hr/>
                            <ModalBody>
                                <div className="w-full xl:px-20 lg:px-16 md:px-3 sm:px-0 items-center bg-white">
                                    <div className="w-full xl:px-4 lg:px-5 md:px-1 items-center bg-white">
                                        <div className="container mx-auto">
                                            <Form className="max-w-full mx-auto p-16">
                                                <div className="flex flex-col w-full">
                                                    <FastField
                                                        name="name"
                                                        component={InputField}
                                                        placeholder="Name"
                                                    />
                                                    <FastField
                                                        name="price"
                                                        component={InputField}
                                                        placeholder="Price"
                                                    />
                                                    <FastField
                                                        name="description"
                                                        component={InputField}
                                                        placeholder="Description"
                                                    />
                                                    <FastField
                                                        name="capacity"
                                                        component={SelectField}
                                                        placeholder="Capacity"
                                                        options={GUEST_OPTIONS}
                                                    />
                                                    <FormGroup>
                                                        <Button
                                                            type="submit"
                                                            className="w-full"
                                                            color="primary"
                                                            onClick={toggleShow}
                                                        >
                                                            {isSubmitting && <Spinner size="sm"/>}
                                                            Update
                                                        </Button>
                                                    </FormGroup>
                                                </div>
                                            </Form>
                                        </div>
                                    </div>
                                </div>
                            </ModalBody>
                            <ModalFooter>
                                <Button onClick={toggleShow}>Close</Button>
                            </ModalFooter>
                        </Modal>
                    </div>
                )
            }}
        </Formik>
    );
}

export default RoomEditForm;
