/* eslint-disable react-hooks/exhaustive-deps */
/* eslint-disable no-unused-vars */
/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { useState, useEffect } from 'react';
import { useSecureLs } from '../../../Global/UseSecureLs'
import { useDispatch, useSelector } from 'react-redux'
import { adminLogin } from '../../../../redux/actions/auth.action'
import { useHistory } from 'react-router-dom'
import { Loading } from '../../../Global/Loading'
import StaticHeader from '../../../Global/StaticHeader'
import ErrorMessage from '../../../Global/ErrorMessage'


function AdminLogin(props) {

    const dispatch = useDispatch();
    const state = useSelector((state) => state);

    const [user, setUser] = useState({
        email: "",
        password: "",
    })

    const [token, setToken] = useSecureLs("token");
    const [userId, setUserId] = useSecureLs("user_id");

    const handleSubmit = (e) => {
        e.preventDefault();
        adminLogin(dispatch, user, setToken, setUserId);
    }

    const history = useHistory();
    useEffect(() => {
        state.auth.isAuthenticated && history.push('/admin');
    }, [state.auth.isAuthenticated]);

    useEffect(() => {
        document.title = "VIBO | Login as an owner";
    }, []);

    return (
        <div>
            <StaticHeader/>
            <div className="bg-gray-100 flex flex-col justify-center items-center py-32">
                {state.auth.loading && <Loading />}
                {state.auth.success === false && (
                    <ErrorMessage errors={state.auth.errors} />
                )}
                <h1 className="text-3xl font-semibold text-center">
                    Sign In To Your Account
                </h1>
                <form className="w-11/12 sm:w-10/12 lg:w-6/12 xl:w-4/12 bg-gray-200 p-10 rounded-sm  mt-10 border shadow-lg"
                    onSubmit={handleSubmit}
                >
                    <input
                        id="email"
                        type="email"
                        className="p-2 w-full border border-gray-400 focus:outline-none focus:border-black"
                        aria-required="true"
                        placeholder="Email"
                        onChange={(e) =>
                            setUser({ ...user, email: e.target.value })
                        }
                    />
                    <input
                        id="password"
                        type="password"
                        aria-required="true"
                        className="p-2 w-full border border-gray-400 focus:outline-none focus:border-black"
                        placeholder="Password"
                        onChange={(e) =>
                            setUser({ ...user, password: e.target.value })
                        }
                    />
                    <div className="mt-5 flex flex-col md:flex-row justify-between items-center">
                        <button 
                            className="text-center bg-yellow-600 text-white hover:bg-yellow-700 uppercase text-sm px-12 py-4 shadow hover:shadow-lg" 
                            type="submit"
                        >
                            Sign in
                        </button>
                    </div>
                </form>
            </div>  
        </div>
    );
};

export default AdminLogin;
