import React from 'react'
import PropTypes from 'prop-types'
import {Formik, Form, FastField} from 'formik'
import {Button, FormGroup, Spinner} from 'reactstrap'
import {DatePicker} from 'antd'
import * as Yup from 'yup'

import SelectField from '../../custom-fields/SelectField'
import {CITY_OPTIONS} from '../../constants/global'
import {GUEST_OPTIONS} from '../../constants/global'

Searchbar.propTypes = {
    onSubmit: PropTypes.func,
}

Searchbar.defaultProps = {
    onSubmit: null,
}

export default function Searchbar(props) {
    const initialValues = {
        checkIn: '',
        checkOut: '',
        cityId: null,
        guest: null,
    }

    const validationSchema = Yup.object().shape({
        checkIn: Yup.string().required('This field is required'),
        checkOut: Yup.string().required('This field is required'),
        city: Yup.number().required('This field is required').nullable(),
        guest: Yup.number().required('This field is required').nullable(),
    })

    return (
        <Formik
            initialValues={initialValues}
            validationSchema={validationSchema}
            onSubmit={props.onSubmit}
        >
            {formikProps => {
                const {values, errors, touched, isSubmitting} = formikProps;
                console.log({values, errors, touched});

                return (
                    <Form>
                        FastField
                        <DatePicker

                        />

                        <FastField 
                            name="cityId"
                            component={SelectField}

                            label="City"
                            placeholder="Which city do you want to go?"
                            options={CITY_OPTIONS}
                        />   

                        <FastField 
                            name="guest"
                            component={SelectField}

                            label="Guest"
                            placeholder="The number of guests?"
                            options={GUEST_OPTIONS}
                        />    

                        <FormGroup>
                            <Button 
                                type="submit"
                                color="primary"
                            >
                                {isSubmitting && <Spinner size="sm"/>}
                                 Find
                            </Button>
                        </FormGroup>
                    </Form>
                )
            }}
        </Formik>
    )
}
