import React, {useState} from "react";
import Slider from "react-slick";

import "react-image-lightbox/style.css";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import Lightbox from "react-image-lightbox";

export default function SimpleSlider(props) {
    const [photoIndex, setPhotoIndex] = useState(0);
    const [isOpen, setIsOpen] = useState(false);
    const [images, setImages] = useState([]);


    const componentDidUpdate = (prevProps) => {
        if (props.images !== prevProps.images) {
            setImages({
                ...images,
                images: props.images
            });
        }
    }
    const url = process.env.REACT_APP_API_URL;

    var settings = {
        infinite: true,
        slidesToShow: 2,
        slidesToScroll: 1,
        autoplay: true,
        speed: 3000,
        autoplaySpeed: 3000,
        arrows: true,
        className: "rounded-md overflow-x-hidden shadow-xl",
        responsive: [
            {
                breakpoint: 600,
                settings: {
                    slidesToShow: 1
                }
            },
            {
                breakpoint: 480,
                settings: {
                    slidesToShow: 1
                }
            }
        ]
    };
    return(
            <div>
                {images.length !== 0 && (
                    <div className="mt-10 p-0" data-aos="fade-up">
                        {isOpen && (
                            <Lightbox
                                mainSrc={`${url}/customer/locations/${images[photoIndex].id}`}
                                nextSrc={
                                    images[(photoIndex + 1) % images.length]
                                }
                                prevSrc={
                                    images[
                                        (photoIndex + images.length - 1) %
                                            images.length
                                    ]
                                }
                                onCloseRequest={() =>
                                    setIsOpen({
                                        ...isOpen,
                                        isOpen: false
                                    })
                                }
                                onMovePrevRequest={() =>
                                    this.setState({
                                        ...this.state,
                                        photoIndex:
                                            (photoIndex + images.length - 1) %
                                            images.length
                                    })
                                }
                                onMoveNextRequest={() =>
                                    this.setState({
                                        ...this.state,
                                        photoIndex:
                                            (photoIndex + 1) % images.length
                                    })
                                }
                            />
                        )}
                        <div className="mt-5 md:mt-0 relative p-0">
                            <Slider {...settings}>
                                {images.map((element, i) => (
                                    <img
                                        src={`${url}/customer/locations/${element}.id`}
                                        key={i}
                                        alt="slider"
                                        className="h-96 w-full object-cover cursor-pointer p-1"
                                        onClick={() =>
                                            this.setState({
                                                ...this.state,
                                                photoIndex: i,
                                                isOpen: true
                                            })
                                        }
                                    />
                                ))}
                            </Slider>
                        </div>
                    </div>
                )}
            </div>
    )
}
