import React, { useEffect, useState } from "react";
import HotelCard from "./HotelCard";
import { getHotels, getPagination } from "../../../redux/actions/hotel.action";
import { useSelector, useDispatch } from "react-redux";
import Pagination from "../../../components/Pagination";

function Hotels() {
    const dispatch = useDispatch();
    const state = useSelector((state) => state);
    useEffect(() => getPagination(dispatch), [dispatch]);

    const {page, count, total} = state.hotel.pagination;

    const [pagination, setPagination] = useState({
        _page: page,
        _count: count,
        _total: total,
    });

    const handlePageChange = (newPage) => {
        setPagination({
            ...pagination,
            _page: newPage
        })
    }

    useEffect(() => getHotels(dispatch, page), []);



    return (
        <section className="px-12 py-10 xl:px-32">
            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4">
                {state && state.hotel.hotels.map((hotel) => (
                    !hotel.isFeatured ? (
                        <HotelCard hotel={hotel} key={hotel.id} />
                    ) : ""
                ))}

                <Pagination
                    pagination = {pagination}
                    onPageChange = {handlePageChange}
                />
            </div>
        </section>
    );
}

export default Hotels;
