import React, { useState } from "react";
import LoginForm from "./LoginForm";
import SignupForm from "./SignupForm";
import StaticHeader from '../../components/StaticHeader'
import { AccountContext } from "./accountContext";
import { Link } from "react-router-dom"

import './Login.css'

const BackDrop = styled(motion.div)`
    width: 260%;
    height: 650px;
    position: absolute;
    display: flex;
    flex-direction: column;
    border-radius: 50%;
    top: -330px;
    left: -150px;
    transform: rotate(60deg);
    background: rgb(34,193,195);
    background: linear-gradient(90deg, #2b5876 0%,  #4e4376 100%);
`;

function Login() {
    const [status, setStatus] = useState("signin");

    const switchToSignup = () => {
        setTimeout(() => {
            setStatus("signup");
        }, 400)
    }

    const switchToSignin = () => {
        setTimeout(() => {
            setStatus("signin");
        }, 400)
    }

    const contextValue = { switchToSignup, switchToSignin };

    return (
        <AccountContext.Provider value={contextValue}>
            <StaticHeader />
            <div className="login-container" data-aos="fade-up">
                <div className="top-container">
                    {status === "signin" && <div className="header-container">
                        <h2 className="header-text">
                            Welcome back
                    </h2>
                        <h4 className="small-text">
                            Please sign in to enjoy our services!
                    </h4>
                    </div>}
                    {status === "signup" && <div className="header-container">
                        <h2 className="header-text">
                            Create an account
                    </h2>
                        <h4 className="small-text">
                            In order to access our services
                    </h4>
                    </div>}
                </div>
                {status === "signin" && <LoginForm />}
                {status === "signup" && <SignupForm />}
                <Link to="/" className="hover:no-underline">
                    <p className="muted-link"> Back to homepage </p>
                </Link>
            </div>
        </AccountContext.Provider>
    );
}

export default Login;
