import React from 'react'
import Title from '../../../components/Title'
import { CITY_OPTIONS } from '../../../constants/global'

function Citys() {
    const citys = CITY_OPTIONS;
    var settings = {
        infinite: true,
        slidesToShow: 3,
        slidesToScroll: 1,
        autoplay: true,
        speed: 2000,
        autoplaySpeed: 2000,
        arrows: true,
        className: "rounded-md overflow-x-hidden shadow-xl",
        responsive: [
            {
                breakpoint: 600,
                settings: {
                    slidesToShow: 2
                }
            },
            {
                breakpoint: 480,
                settings: {
                    slidesToShow: 1
                }
            }
        ]
    };

    return (
        <> 
            <Title title="The most attractive destinations in Vietnam" />
            <div className="flex mx-auto px-20 py-5">
                {citys.map((city) => {
                    return(
                        <div className="w-full mx-auto cursor-pointer px-5">
                            <div className="w-48 h-48 flex items-center justify-center">
                                <img src={city.image} alt="city" className="w-44 h-44 object-cover rounded-full"/>
                            </div>
                            <p className="mx-4 text-lg font-semibold font-serif">
                                {city.label}
                            </p>
                        </div>
                    )
                })}
            </div>
        </>
    )
}

export default Citys;
