export const formatPrice = (price) => {
    let priceString = '';
    price = Math.floor(price);
    while (price > 999) {
        let num = price % 1000;
        priceString += '.' + num;
        price = Math.floor(price / 1000);
        if (price <= 999) {
            priceString = price + '' + priceString;
            break;
        }
    }
    return priceString;
}


export const formatDate = (isoDate) => {
    let year = isoDate.getFullYear;
    let month = isoDate.getMonth;
    let date = isoDate.getDate;
    let hour = isoDate.getHour;
    let minute = isoDate.getMinute;

    if (date < 10) {
        date = '0' + date;
    }
    if (month < 10) {
        month = '0' + month;
    }

    return `${year}-${month}-${date}-${hour}-${minute}`
}
