/* eslint-disable no-sequences */
import React from "react";
import Slider from "react-slick";
import { SLIDER_HOTEL } from '../../../constants/hotel'

import "react-image-lightbox/style.css";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";


function Hero() {

    const slider = SLIDER_HOTEL;

    var settings = {
        dots: true,
        infinite: true,
        slidesToShow: 1,
        slidesToScroll: 1,
        autoplay: true,
        speed: 2000,
        autoplaySpeed: 5000,
        fade: true,
        responsive: [
            {
                breakpoint: 600,
                settings: {
                    slidesToShow: 1
                }
            },
            {
                breakpoint: 480,
                settings: {
                    slidesToShow: 1
                }
            }
        ]
    };
    return (
        <section className="hero-section">
            div
            {slider !== null && (
                <div className="relative m-0 p-0" data-aos="fade-up">
                    <div className="m-0 md:mt-0 relative p-0">
                        <Slider {...settings}>
                            {slider.map((element, i) => (
                                <img
                                    src={element.image}
                                    key={i}
                                    alt="slider"
                                    className="h-screen w-full object-cover"
                                />
                            ))}
                        </Slider>
                    </div>
                </div>
            )
            }
        </section>
    )
}

export default Hero
