import React, { useState, useEffect } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faTimes, faBars, faHome, faUser} from '@fortawesome/free-solid-svg-icons'
import {Link} from 'react-router-dom'
import { useSelector } from "react-redux";
import useSecureLs from "./UseSecureLs";
import SecureLS from "secure-ls";
import Logout from '../Logout'
import './Global.css'



function Header() {

    const state = useSelector((state) => state);
    const [id] = useSecureLs("user_id");
    let ls = new SecureLS({ encodingType: "aes", isCompression: false });

    useEffect(() => {
        let _firstLogin = localStorage.getItem("first_login");
        if (_firstLogin === "true" && state.auth.isAuthenticated) {
            setFirstLogin(true);
        }
    }, []); // eslint-disable-line

    const [firstLogin, setFirstLogin] = useState(false);
    const [isClicked, toggleClick] = useState(false);
    const handleClickChange = () => {
        toggleClick(!isClicked);
    }

    return (
        <div>
            <header className="header-items">
                <h2>VIBO.com</h2>
                <div className="menu-icon" 
                    onClick={handleClickChange}>
                    <FontAwesomeIcon icon={isClicked ? faTimes : faBars } />
                </div>
                <ul className={isClicked ? 'header-menu active': 'header-menu'}>
                    {!state.auth.isAuthenticated ? (
                        <>
                            <li>
                                <Link to="/">
                                    <button className="header-links">
                                        <FontAwesomeIcon icon={faHome} />{" "} Home
                                    </button>
                                </Link>
                            </li>
                            <li>
                                <Link to="/">
                                    <button className="header-links">
                                        <FontAwesomeIcon icon={faUser} />{" "} Login
                                    </button>
                                </Link>
                            </li>
                        </>

                    ) : (
                        <>
                            <li>
                                <Link to="/">
                                    <button className="header-links">
                                        <FontAwesomeIcon icon={faHome} />{" "} Home
                                    </button>
                                </Link>
                            </li>
                            <li>
                                <Link to="/logout">
                                    <button className="header-links">
                                        Hi,{" "} Logout
                                    </button>
                                </Link>
                            </li>
                        </>
                    )
                    }
                </ul>
            </header>
        </div>
    );
}

export default Header;
