/* eslint-disable no-sequences */
import React, { useState, useEffect } from "react";
import Slider from "react-slick";
import Lightbox from "react-image-lightbox";
import { useSelector, useDispatch } from "react-redux"
import { getHotelImages } from "../../../../redux/actions/hotel.action"

import "react-image-lightbox/style.css";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";


function HotelSlider() {
    const dispatch = useDispatch();
    const state = useSelector((state) => state);

    useEffect(() => {
        getHotelImages(dispatch);
    }, []); // eslint-disable-line

    const [photoIndex, setPhotoIndex] = useState(0);
    const [isOpen, setIsOpen] = useState(false);
    const images = state.hotel.images;

    const handleIsOpen = () => {
        setIsOpen(!isOpen);
    }

    const handlePhotoIndex = (value) => {
        setPhotoIndex(value);
    }

    const handleSlider = (value) => {
        setIsOpen(true);
        setPhotoIndex(value);
    }

    var settings = {
        infinite: true,
        slidesToShow: 2,
        slidesToScroll: 1,
        autoplay: true,
        speed: 2000,
        autoplaySpeed: 2000,
        arrows: true,
        className: "rounded-md overflow-x-hidden shadow-xl",
        responsive: [
            {
                breakpoint: 600,
                settings: {
                    slidesToShow: 1
                }
            },
            {
                breakpoint: 480,
                settings: {
                    slidesToShow: 1
                }
            }
        ]
    };
    return (
        <>
            {images !== null && (
                <div className="mt-10 p-0" data-aos="fade-up">
                    {isOpen && (
                        <Lightbox
                            mainSrc={images[photoIndex]}
                            nextSrc={
                                images[(photoIndex + 1) % images.length]
                            }
                            prevSrc={
                                images[
                                (photoIndex + images.length - 1) %
                                images.length
                                ]
                            }
                            onCloseRequest={handleIsOpen}
                            onMovePrevRequest={
                                handlePhotoIndex(
                                    (photoIndex + images.length - 1) % images.length
                                )
                            }
                            onMoveNextRequest={
                                handlePhotoIndex(
                                    (photoIndex + 1) % images.length
                                )
                            }
                        />
                    )}
                    <div className="mt-5 md:mt-0 relative p-0">
                        <Slider {...settings}>
                            {images.map((element, i) => (
                                <img
                                    src={element}
                                    key={i}
                                    alt="slider"
                                    className="h-96 w-full object-cover cursor-pointer p-1"
                                    onClick={handleSlider(i)}
                                />
                            ))}
                        </Slider>
                    </div>
                </div>
            )
            }
        </>
    )
}

export default HotelSlider
