import React, {useState, useEffect} from "react";
import {useDispatch, useSelector} from "react-redux";
import {Modal, ModalFooter, ModalBody, ModalHeader, FormGroup, Spinner, Button} from "reactstrap"
import PropTypes from "prop-types";
import {Formik, FastField, Form} from "formik";
import InputField from "../../../custom-fields/InputField";
import Title from "../../Global/Title";
import SelectField from "../../../custom-fields/SelectField";
import {CITY_OPTIONS} from "../../../constants/global";

UserPersonalInfo.defaultProps = {
    onSubmit: null,
    user: null,
}

UserPersonalInfo.propTypes = {
    onSubmit: PropTypes.func.isRequired,
    user: PropTypes.object.isRequired,
}

function UserPersonalInfo(props) {
    const [show, setShow] = useState(false);
    const toggleShow = () => setShow(!show);
    const initialValues = {
        avatar: "",
        fullName: "",
        phoneNumber: "",
        address: "",
        city: ""
    }

    return (
        <div>
            <Button onClick={toggleShow} color="dark">Change Personal Information</Button>
            <Formik
                initialValues={initialValues}
                onSubmit={props.onSubmit}
            >
                {formikProps => {
                    const {isSubmitting} = formikProps;
                    return (
                        <Modal
                            size="lg"
                            aria-labelledby="contained-modal-title-vcenter"
                            centered
                            isOpen={show} toggle={toggleShow}
                        >
                            <ModalHeader closeButton>
                                <Title title="Update personal information"/>
                            </ModalHeader>
                            <ModalBody>
                                <div className="w-full xl:px-20 lg:px-16 md:px-3 sm:px-0 items-center bg-white">
                                    <div className="w-full xl:px-4 lg:px-5 md:px-1 items-center bg-white"
                                         data-aos="fade-up">
                                        <div className="container mx-auto">
                                            <Form inline onSubmit className="max-w-full mx-auto bg-gray-200 p-16 rounded-md shadow-sm">
                                                <div className="flex flex-col w-full">
                                                    <FastField
                                                        name="name"
                                                        component={InputField}
                                                        placeholder="Name"
                                                    />
                                                    <FastField
                                                        name="address"
                                                        component={InputField}
                                                        placeholder="Address"
                                                    />
                                                    <FastField
                                                        name="phoneNumber"
                                                        component={InputField}
                                                        placeholder="Phone Number"
                                                    />
                                                    <FastField
                                                        name="city"
                                                        component={SelectField}
                                                        placeholder="City"
                                                        options={CITY_OPTIONS}
                                                    />
                                                    <FormGroup>
                                                        <Button
                                                            type="submit"
                                                            className="w-full"
                                                            color="primary"
                                                            onClick={toggleShow}
                                                        >
                                                            {isSubmitting && <Spinner size="sm"/>}
                                                            Update
                                                        </Button>
                                                    </FormGroup>
                                                </div>
                                            </Form>
                                        </div>
                                    </div>
                                </div>
                            </ModalBody>
                            <ModalFooter>
                                <Button onClick={toggleShow}>Close</Button>
                            </ModalFooter>
                        </Modal>
                    )
                }}
            </Formik>
        </div>

    );
}

export default UserPersonalInfo;
