/* eslint-disable no-sequences */
/* eslint-disable no-unused-vars */
import React, {useState} from "react";
import Slider from "react-slick";

import "react-image-lightbox/style.css";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import Lightbox from "react-image-lightbox";

export default function SimpleSlider(props) {
    const [photoIndex, setPhotoIndex] = useState(0);
    const [isOpen, setIsOpen] = useState(false);
    const [images, setImages] = useState([
        "//static.asiawebdirect.com/m/bangkok/portals/vietnam/homepage/da-nang/top10/top10-da-nang-hotels/pagePropertiesImage/danang-best-hotels.jpg",
        "//cdn.cnn.com/cnnnext/dam/assets/160523150838-6--intercontinental-danang-full-169.jpg",
        "//tapchitoaan.vn/uploads/2020/06/DJI_0040.jpg",
        "//digital.ihg.com/is/image/ihg/crowne-plaza-danang-2532263612-4x3?qlt=85,0&resMode=sharp2&op_usm=1.75,0.9,2,0"
    ]);

    const componentDidUpdate = (prevProps) => {
        if (props.images !== prevProps.images) {
            setImages({
                images: props.images
            });
        }
    }

    var settings = {
        infinite: true,
        slidesToShow: 2,
        slidesToScroll: 1,
        autoplay: true,
        speed: 3000,
        autoplaySpeed: 3000,
        arrows: true,
        className: "rounded-md overflow-x-hidden shadow-xl",
        responsive: [
            {
                breakpoint: 600,
                settings: {
                    slidesToShow: 1
                }
            },
            {
                breakpoint: 480,
                settings: {
                    slidesToShow: 1
                }
            }
        ]
    };
    return(
            <>
                 {images.length !== 0 && (
                         <div className="mt-10 p-0" data-aos="fade-up">
                        {isOpen && (
                            <Lightbox
                                mainSrc={images.photoIndex}
                                nextSrc={
                                    images[(photoIndex + 1) % images.length]
                                }
                                prevSrc={
                                    images[
                                        (photoIndex + images.length - 1) %
                                            images.length
                                    ]
                                }
                                onCloseRequest={() =>
                                    setIsOpen({
                                        isOpen: false
                                    })
                                }
                                onMovePrevRequest={() =>
                                    setPhotoIndex({
                                        photoIndex:
                                            (photoIndex + images.length - 1) %
                                            images.length
                                    })
                                }
                                onMoveNextRequest={() =>
                                    setPhotoIndex({
                                        photoIndex:
                                            (photoIndex + 1) % images.length
                                    })
                                }
                            />
                        )}
                        <div className="mt-5 md:mt-0 relative p-0">
                            <Slider {...settings}>
                                {images.map((element, i) => (
                                    <img
                                        src={element}
                                        key={i}
                                        alt="slider"
                                        className="h-96 w-full object-cover cursor-pointer p-1"
                                        onClick={() =>
                                            setIsOpen({
                                                isOpen: true,
                                            }),
                                            setPhotoIndex({
                                                photoIndex: i,
                                            })
                                        }
                                    />
                                ))}
                            </Slider>
                        </div>
                     </div>
                    )
                }
            </>
    )
}
