import React, {useState, useEffect} from "react";
import {useDispatch, useSelector} from "react-redux";
import {updateUserInfo} from "../../../redux/actions/user.action";
import {Modal, ModalFooter, ModalBody, ModalHeader} from "reactstrap"
import Title from "../../Global/Title";
import {Button} from "antd";
import PropTypes from "prop-types";

UserPersonalInfo.defaultProps = {
    onSubmit: null,
    user: null,
}

UserPersonalInfo.propTypes = {
    onSubmit: PropTypes.func.isRequired,
    user: PropTypes.object.isRequired,
}

function UserPersonalInfo(props) {
    const dispatch = useDispatch();
    const state = useSelector((state) => state);
    const [show, setShow] = useState(false);
    const toggleShow = () => setShow(!show);
    const initialValues = {
        avatar: "",
        fullName: "",
        phoneNumber: "",
        address: "",
        city: ""
    }

    const [userInfo, setUserInfo] = useState(initialValues);
    const [image, setImage] = useState(null);

    useEffect(() => {
        setUserInfo({
            fullName: props.user && props.user.fullName ? props.user.fullName : "",
            phoneNumber: props.user && props.user.phoneNumber ? props.user.phoneNumber : "",
            address: props.user && props.user.address ? props.user.address : "",
            city: props.user && props.user.city ? props.user.city : ""
        });
    }, [props.user]); // eslint-disable-line

    const onSubmitHandler = (e) => {
        updateUserInfo(dispatch, state.auth.token, userInfo);
    };
    return (
        <div>
            <Button onClick={toggleShow} variant="dark">Change Personal Information</Button>
            <Modal
                size="lg"
                aria-labelledby="contained-modal-title-vcenter"
                centered
                isOpen={show} toggle={toggleShow}
            >
                <ModalHeader closeButton>
                    <Title id="contained-modal-title-vcenter">
                        Change your personal info
                    </Title>
                </ModalHeader>
                <ModalBody>
                    <form
                        onSubmit={onSubmitHandler}
                        className="mx-5"
                    >
                        <div className="flex items-center">
                            <img
                                src={
                                    props.user && props.user.image
                                        ? props.user.image
                                        : "http://placehold.it/300x300?text=avatar"
                                }
                                alt="avatar"
                                className="w-32 h-32 rounded-full object-cover"
                            />

                            <label aria-label="upload image"
                                   className="ml-5 px-5 py-2 text-gray-200 bg-orange-500 hover:bg-orange-900 rounded-sm cursor-pointer">
                                <input
                                    type="file"
                                    name=""
                                    id=""
                                    className="hidden"
                                    onChange={(e) => {
                                        setImage(e.target.files[0]);
                                    }}
                                />
                                <i className="fas fa-camera mr-2"/>
                                <span>{image ? image.name : "Upload"}</span>
                            </label>
                        </div>
                        <input
                            type="text"
                            name="fullName"
                            id="fullName"
                            className="p-2 w-full xl:w-1/2 border border-gray-400 focus:outline-none focus:border-black"
                            value={userInfo.fullName}
                            onChange={(e) =>
                                setUserInfo({...userInfo, first_name: e.target.value})
                            }
                        />

                        <label htmlFor="last_name" className="block mt-5">
                            Last Name:{" "}
                        </label>
                        <input
                            type="text"
                            id="last_name"
                            name="last_name"
                            className="p-2 w-full xl:w-1/2 border border-gray-400 focus:outline-none focus:border-black"
                            value={userInfo.last_name}
                            onChange={(e) =>
                                setUserInfo({...userInfo, last_name: e.target.value})
                            }
                        />

                        <label htmlFor="phone" className="block mt-5">
                            Phone:{" "}
                        </label>
                        <input
                            type="tel"
                            id="phone"
                            name="phone"
                            className="p-2 w-full xl:w-1/2 border border-gray-400 focus:outline-none focus:border-black"
                            value={userInfo.phone}
                            onChange={(e) =>
                                setUserInfo({...userInfo, phone: e.target.value})
                            }
                        />

                        <label htmlFor="address" className="block mt-5">
                            Address:{" "}
                        </label>
                        <input
                            type="text"
                            name="address"
                            id="address"
                            className="p-2 w-full xl:w-1/2 border border-gray-400 focus:outline-none focus:border-black"
                            value={userInfo.address}
                            onChange={(e) =>
                                setUserInfo({...userInfo, address: e.target.value})
                            }
                        />
                    </form>
                </ModalBody>
                <ModalFooter>
                    <Button
                        variant="dark"
                        type="submit"
                        onClick={toggleShow}
                    >
                        Update
                    </Button>
                    <Button onClick={toggleShow}>Close</Button>
                </ModalFooter>
            </Modal>
        </div>

    );
}

export default UserPersonalInfo;
