import React from 'react';
import Title from "../../Global/Title";
import {Table} from "reactstrap";
import {formatPrice} from "../../../constants/function";
import BookingModal from "../../Hotel/BookingModal";
import {useSelector} from "react-redux";

function UserBooking(props) {

    const state = useSelector((state) => state);
    return (
        <div className="w-full md:w-6/12 xl:w-8/12 md:ml-5 bg-gray-200 p-5 rounded-sm ">
            {state.user.user && (
                <Title title={`${state.user.user.fullName}'s Booking Histories`} data-aos="fade-up"/>
            )}
            <div className="lg:mx-20 xl:mx-40">
                <Table striped bordered hover data-aos="fade-up">
                    <thead>
                    <tr>
                        <th>ID</th>
                        <th>Name</th>
                        <th>Maximum guests</th>
                        <th>Price</th>
                        <th>Book</th>
                    </tr>
                    </thead>
                    <tbody>
                    {state.hotel.hotel && state.hotel.hotel.rooms.map((room) => {
                        return (
                            <tr key={room.id}>
                                <td className="grid grid-cols-5">
                                    {state.hotel.hotel.serviceTypes.map((service, key) => {
                                        return (
                                            <div key={key}>
                                                <img
                                                    src={service.icon}
                                                    alt="service"
                                                    className="relative w-6 h-6 object-contain"
                                                />
                                            </div>
                                        )
                                    })}
                                </td>
                                <td>{room.name}</td>
                                <td>{room.capacity}</td>
                                <td>{formatPrice(room.price)} {" "} VND</td>
                                <td>
                                    <BookingModal room={room}/>
                                </td>
                            </tr>
                        )
                    })}
                    </tbody>
                </Table>
            </div>
        </div>
    );
}

export default UserBooking;
