        infinite: true,
        slidesToShow: 2,
        slidesToScroll: 1,
        autoplay: true,
        speed: 3000,
        autoplaySpeed: 3000,
        arrows: true,
        className: "rounded-md overflow-x-hidden shadow-xl",
        responsive: [
            {
                breakpoint: 600,
                settings: {
                    slidesToShow: 1
                }
            },
            {
                breakpoint: 480,
                settings: {
                    slidesToShow: 1
                }
            }
        ]
    };
    return(
            <div>
                {state.hotel.images.map((hotel) =>{
                    return(
                        <div className="mt-10 p-0" data-aos="fade-up">
