                            //         setPhotoIndex({
                            //             photoIndex:
                            //                 (photoIndex + 1) % images.length
                            //         })
                            //     }
                            // />
                        )} */}
                        <div className="mt-5 md:mt-0 relative p-0">
                            <Slider {...settings}>
                                {hotel.images.map((element, i) => (
                                    <img
                                        src={element}
                                        key={i}
                                        alt="slider"
                                        className="h-96 w-full object-cover cursor-pointer p-1"
                                        onClick={() =>
                                            setIsOpen({
                                                isOpen: true,
                                            })
                                        }
                                    />
                                ))}
                            </Slider>
                        </div>
                    </div>
                    )
                })}
            </div>
    )
}
