/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { useState, useContext, useEffect } from 'react';
import {useSecureLs} from '../Global/UseSecureLs'
import {useDispatch, useSelector} from 'react-redux'
import {AccountContext} from './accountContext';
import {signUp} from '../../redux/actions/auth'
import {useHistory, Link} from 'react-router-dom'
import {Loading} from '../Global/Loading'
import {ErrorMessage} from '../Global/ErrorMessage'

import './Login.css'
import JSONDATA from '../Home/data.json'


function SignupForm(props){
    const {switchToSignin} = useContext(AccountContext);

    const dispatch = useDispatch();
    const state = useSelector((state) => state);
    const [token, setToken] = useSecureLs("token");
    const [userId, setUserId] = useSecureLs("token");

    const [user, setUser] = useState({
        name: "",
        email: "",
        password: "",
        address: "",
        city: "",
    })

    const handleSubmit = (e) => {
        e.preventDefault();
        signUp(dispatch, user, setToken, setUserId);
    };

    const history = useHistory();
    useEffect(() => {
        if (state.auth.isAuthenticated){
            history.push("/");
        } else {
            history.push("/login");
        }
    })

    useEffect(() =>{
        document.title = "VIBO | Sign up";
    });

    return (
        <div class="inner-container">
            {state.auth.loading && <Loading />}

            {state.auth.success === false && (
                <ErrorMessage errors={state.auth.errors} />
            )}
            <div className="box-container">
                <form className="form-container" onSubmit={handleSubmit}>
                    <input
                        id="name"
                        type="text" 
                        className="input"
                        placeholder="Full name"
                        onChange={(e) => 
                            setUser({ ... user, name: e.target.value})
                        }
                    />
                    <input 
                        type="email" 
                        className="input"
                        placeholder="Email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                    />
                    <input 
                        type="text" 
                        className="input"
                        placeholder="Address"
                        value={address}
                        onChange={(e) => setAddress(e.target.value)}
                    />
                    <select
                        placeholder="City"
                        className="input"
                        value={city}
                        onChange={(e) => setCity(e.target.value)}
                    >
                        <option value="all">All Cities</option>
                        {
                            JSONDATA.map((val, key) => {
                                return (
                                    <option value={val.city} key={key}>
                                        {val.city}
                                    </option>
                                );
                            })}
                    </select>
                    <input 
                        type="password" 
                        className="input"
                        placeholder="Password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    <button className="submit-button" type="submit">
                        Sign up
                    </button>
                </form>
                <a className="muted-link">
                        Already have an account?
                        <a 
                            className="bold-link"
                            onClick={switchToSignin}
                        >
                            Sign-in
                        </a>
                </a>
            </div>
        </div>
    );
};

export default SignupForm;
