import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { getCityHotels } from '../../redux/actions/hotel.action'
import { useHistory, useParams } from 'react-router-dom'
import Title from "../../Global/Title"
import HotelCard from './HotelCard'

export default function City() {
    const dispatch = useDispatch();
    const { id } = useParams();
    const state = useSelector(state => state.state);

    useEffect(() => {
        getCityHotels(dispatch, id);
    }, []); // eslint-disable-line

    let history = useHistory();
    useEffect(() => {
        const timer = setTimeout(() => {
            if (!state.hotel.cityHotels) {
                history.push({
                    pathname: "/404",
                    state: {
                        message:
                            "There is no such city, you'll be redirected in a bit"
                    }
                });
            }
        }, 3000);
        // document.title = `${state.hotel.hotel.name}`;
        return () => clearTimeout(timer);
    }, [state.hotel.cityHotels]); // eslint-disable-line
    return (
        <div>
            <section className="px-20 py-12 md:px-40 lg:px-56">
                <Title title="Featured Hotels" />
                <div className="grid grid-cols-1 sm:grid-cols-1 md:grid-cols-2 gap-4">
                    {state && state.hotel.allHotels.map((hotel) => (
                        <HotelCard hotel={hotel} key={hotel.id} />
                    ))}
                </div>
            </section>
        </div>
    )
}
