import React from 'react';
import Slider from "react-slick";

function LocationInfo({location:{images, name, address, rooms}}) {
    const settings = {
        autoplay: true,
        infinite: true,
        slidesToShow: 3,
        slidesToScroll: 1,
        speed: 4000,
        autoplaySpeed: 4000,
        arrows: true,
        className: "overflow-x-hidden",
        responsive: [
            {
                breakpoint: 1024,
                settings: {
                    slidesToShow: 2
                }
            },

            {
                breakpoint: 600,
                settings: {
                    slidesToShow: 1
                }
            }
        ]
    };
    return (
        <div className="w-8/12 bg-gray-200 p-5 mx-auto rounded-sm">
            <div className="relative mx-auto">
                {images.map((image, key) => {
                    return (
                        <img
                            src={
                                image
                                    ? image
                                    : "http://placehold.it/300x300?text=hotel"
                            }
                            alt="hotel"
                            key={key}
                            className="w-48 h-48 object-cover"
                        />
                    )
                })}
            </div>
        </div>
    );
}

export default LocationInfo;
