import React, {useEffect} from "react";
import {Link, Redirect, Route, Switch, useParams, useRouteMatch} from "react-router-dom";
import {useHistory} from "react-router-dom";
import {useSelector} from "react-redux";
import {Button} from "reactstrap"
import HomeAdmin from "../Admin/pages/HomeAdmin";
import RoomManagement from "../Admin/pages/RoomManagement";
import EditRoom from "../Admin/pages/EditRoom";
import BookingManagement from "../Admin/pages/BookingManagement";

function UserSideBar() {
    const state = useSelector((state) => state);
    const match = useRouteMatch();
    console.log({ match });
    const Error = () => {
        return(
            <h1>
                Page is not found
            </h1>
        )
    }

    let {id} = useParams();
    let history = useHistory();

    useEffect(() => {
        // eslint-disable-next-line
        if (state.auth.user_id !== id) {
            history.push({
                pathname: "/401",
                state: {
                    message:
                        "You are not authorized, you'll be redirected in a bit"
                }
            });
        }
    }, [state.auth.user_id]); // eslint-disable-line

    return (
        <div className="hidden md:block w-full md:w-4/12 xl:w-3/12 bg-gray-200 pt-5 lg:p-6 rounded-sm ">
            <Link
                to={`/user/${id}`}
            >
                <Button active className="block py-3 px-4">
                    My Profile
                </Button>
            </Link>

            <div className="hidden md:block border-t w-2/3 my-2"/>

            <Link
                to={`/user/${id}/bookings`}

            >
                <Button className="block py-3 px-4">
                    My Bookings
                </Button>
            </Link>

            <div className="hidden md:block border-t w-2/3 my-2"/>

            <Button className="block py-3 px-4">
                Change password
            </Button>
            <Switch>
                <Redirect exact from={`${match.url}`} to={`${match.url}/room/`}/>
                <Route exact path={`${match.url}`} component={HomeAdmin} />
                <Route path={`${match.url}/room/`} exact component={RoomManagement} />
                <Route path={`${match.url}/room/:id`} exact component={EditRoom} />
                <Route path={`${match.url}/booking`} exact component={BookingManagement} />
                <Route exact component={Error} />
            </Switch>
        </div>
    );
}

export default UserSideBar;
