/* eslint-disable no-unused-vars */
import React, {useState} from 'react'
import PropTypes from 'prop-types'
import {Formik, Form, FastField} from 'formik'
import {Button, FormGroup, Spinner} from 'reactstrap'
import {DatePicker} from 'antd'
import {Link} from 'react-router-dom'
import moment from 'moment'
import * as Yup from 'yup'

import SelectField from '../../../../custom-fields/SelectField'
import {CITY_OPTIONS} from '../../../../constants/global'
import {GUEST_OPTIONS} from '../../../../constants/global'

Searchbar.propTypes = {
    onSubmit: PropTypes.func,
}

Searchbar.defaultProps = {
    onSubmit: null,
}
const {RangePicker} = DatePicker;

export default function Searchbar(props) {
    const initialValues = {
        fromTo: '',
        cityId: null,
        guest: null,
    }

    const [date, setDate] = useState("");
    const [dateString, setDateString] = useState("");

    const onDateSelection = (value, dateString) => {
        setDate(value);
        setDateString(dateString);
        initialValues.fromTo = dateString;

        console.log("Change time: ", initialValues.fromTo)
    }

    const disablePastDates = (current) => {
        return current && current < moment().endOf("day");
    }

    const validationSchema = Yup.object().shape({
        fromTo: Yup.string().required('This field is required').nullable(),
        cityId: Yup.number().required('This field is required').nullable(),
        guest: Yup.number().required('This field is required').nullable(),
    })

    return (
        <Formik
            initialValues={initialValues}
            validationSchema={validationSchema}
            onSubmit={props.onSubmit}
        >
            {formikProps => {
                const {isSubmitting} = formikProps;

                return (
                    <section className="relative">
                        <div className="overlay w-full h-screen bg-gray-900 absolute z-10 opacity-75"></div>

                        <img
                            src="../../../../assets/images/hotel-1.jpeg"
                            className="w-full h-screen object-cover"
                            alt="hotel cover"
                        />

                        <div className="mt-8 book flex flex-col justify-center items-center text-gray-100 z-20 w-10/12">
                            <h1 className="mt-16 hidden sm:block text-xl md:text-4xl xl:text-6xl capitalize text-center font-serif">
                                Enjoy amazing experience in Vietnam
                            </h1>

                            <div className="bg-orange-500 bg-opacity-75 mt-5 p-5 rounded-sm mx-auto text-gray-700 w-11/12 sm:w-9/12 md:w-8/12 lg:w-7/12 xl:w-9/12 font-sans">
                                <Form className="flex flex-col xl:flex-row items-center">
                                    <div className="flex flex-col md:mr-3 w-full md:w-3/4 ">
                                        <label htmlFor='from-to' className="text-gray-100 text-xl">
                                            Check In - Check Out
                                        </label>
                                        <RangePicker
                                            id="fromTo"
                                            format="MMM Do"
                                            allowClear="true"
                                            placeholder={["Check in", "Check out"]}
                                            defaultValue={moment()}
                                            onChange={onDateSelection}
                                            disabledDate={current => disablePastDates(current)}
                                        />
                                    </div>
                                    <div className="flex flex-col md:mr-3 w-full md:w-3/4 ">
                                        <FastField 
                                            name="cityId"
                                            component={SelectField}

                                            label="City"
                                            placeholder="Which city do you want to go?"
                                            options={CITY_OPTIONS}
                                        />
                                    </div>
                                    <div className="flex flex-col md:mr-3 w-full md:w-3/4 ">
                                    <FastField 
                                        name="guest"
                                        component={SelectField}

                                        label="Guest"
                                        placeholder="The number of guests?"
                                        options={GUEST_OPTIONS}
                                    />
                                    </div>
                                </Form>
                            </div>

                            <hr className="hidden xl:block my-5 w-4/12 mx-auto border-orange-800" />

                            <FormGroup>
                                <Button 
                                    type="submit"
                                    color="primary"
                                >
                                    {isSubmitting && <Spinner size="sm"/>}
                                    <Link
                                        to={`/search/from_to=${initialValues.fromTo}
                                        &city=${initialValues.cityId}
                                        &guest=${initialValues.guest}`}
                                    >
                                        Find
                                    </Link>
                                </Button>
                            </FormGroup>
                        </div>
                    </section> 
                )
            }}
        </Formik>
    )
}
