import React from 'react'
import Title from '../../Global/Title'
import {Link} from 'react-router-dom'
import Slider from "react-slick";


function CityCard({city: {name, thumbnail}}) {
    const settings = {
        autoplay: true,
        infinite: true,
        slidesToShow: 4,
        slidesToScroll: 1,
        speed: 3000,
        autoplaySpeed: 3000,
        arrows: true,
        className: "overflow-x-hidden",
        responsive: [
            {
                breakpoint: 1024,
                settings: {
                    slidesToShow: 3
                }
            },

            {
                breakpoint: 600,
                settings: {
                    slidesToShow: 2
                }
            }
        ]
    };

    return (
        <>
            <Title title="The most attractive destinations in Vietnam"/>
            <div className="flex mx-auto px-8 py-5" data-aos="fade-up">
                <Slider {...settings}>
                    {thumbnail && thumbnail.map((city, key) => {
                        return (
                            // <Link to={`/city/${city.id}`}
                            //       className="no-underline hover:no-underline text-black hover:text-gray-700 px-8 mx-auto">
                            //     <div className="w-full mx-auto cursor-pointer px-4" key={city.id}>
                            //         <div className="w-60 h-60 flex items-center justify-center">
                            //             <img src={city.thumbnail} alt="city"
                            //                  className="w-56 h-56 object-cover rounded-full hover:shadow-2xl"/>
                            //         </div>
                            //         <p className="mx-16 mt-3 text-lg font-semibold font-serif">
                            //             {city.name}
                            //         </p>
                            //         {/* <span className="text-md font-sans font-bold mx-10"
                            //     >{city.averagePrice.slice(1)} VND </span> */}
                            //     </div>
                            // </Link>
                            <img
                                src='http://placehold.it/500x500?text=hotel'
                                alt="city"
                                key={key}
                                className="w-56 h-56 object-cover rounded-full hover:shadow-2xl"
                            />
                        )
                    })}
                </Slider>
            </div>
        </>
    )
}

export default CityCard;
