import React from "react";
import Slider from "react-slick";

import "react-image-lightbox/style.css";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import Lightbox from "react-image-lightbox";
import hotelhotelImages from '../../constants/hotel'

export default class SimpleSlider extends React.Component {
    state = {
        photoIndex: 0,
        isOpen: false,
    };

    render() {
        const { photoIndex, isOpen} = this.state;
        // const url = process.env.REACT_APP_BASE_URL;

        var settings = {
            infinite: true,
            slidesToShow: 3,
            slidesToScroll: 1,
            autoplay: true,
            speed: 2000,
            autoplaySpeed: 2000,
            arrows: true,
            className: "rounded-md overflow-x-hidden shadow-xl",
            responsive: [
                {
                    breakpoint: 600,
                    settings: {
                        slidesToShow: 2
                    }
                },
                {
                    breakpoint: 480,
                    settings: {
                        slidesToShow: 1
                    }
                }
            ]
        };
        return (
            <div>
                {hotelhotelImages.length !== 0 && (
                    <div className="mt-10" data-aos="fade-up">
                        {isOpen && (
                            <Lightbox
                                mainSrc={hotelhotelImages[photoIndex]}
                                nextSrc={
                                    hotelhotelImages[(photoIndex + 1) % hotelhotelImages.length]
                                }
                                prevSrc={
                                    hotelhotelImages[
                                        (photoIndex + hotelImages.length - 1) %
                                            hotelImages.length
                                    ]
                                }
                                onCloseRequest={() =>
                                    this.setState({
                                        ...this.state,
                                        isOpen: false
                                    })
                                }
                                onMovePrevRequest={() =>
                                    this.setState({
                                        ...this.state,
                                        photoIndex:
                                            (photoIndex + hotelImages.length - 1) %
                                            hotelImages.length
                                    })
                                }
                                onMoveNextRequest={() =>
                                    this.setState({
                                        ...this.state,
                                        photoIndex:
                                            (photoIndex + 1) % hotelImages.length
                                    })
                                }
                            />
                        )}
                        <div className="mt-5 md:mt-0 relative">
                            <Slider {...settings}>
                                {hotelImages.map((element, i) => (
                                    <img
                                        src={hotelImages}
                                        key={i}
                                        alt="slider"
                                        className="h-64 w-full object-cover cursor-pointer"
                                        onClick={() =>
                                            this.setState({
                                                ...this.state,
                                                photoIndex: i,
                                                isOpen: true
                                            })
                                        }
                                    />
                                ))}
                            </Slider>
                        </div>
                    </div>
                )}
            </div>
        );
    }
}
