import React, {useState} from "react";
import {Modal, ModalFooter, ModalBody, ModalHeader, FormGroup, Spinner, Button} from "reactstrap"
import PropTypes from "prop-types";
import {Formik, FastField, Form} from "formik";
import InputField from "../../../custom-fields/InputField";
import Title from "../../Global/Title";
import SelectField from "../../../custom-fields/SelectField";
import {CITY_OPTIONS} from "../../../constants/global";

UserPersonalInfo.defaultProps = {
    onSubmit: null,
}

UserPersonalInfo.propTypes = {
    onSubmit: PropTypes.func.isRequired,
}

function UserPersonalInfo(props) {
    const [modal, setModal] = useState(false);
    const [nestedModal, setNestedModal] = useState(false);
    const [closeAll, setCloseAll] = useState(false);

    const toggle = () => setModal(!modal);
    const toggleNested = () => {
        setNestedModal(!nestedModal);
        setCloseAll(false);
    }
    const toggleAll = () => {
        setNestedModal(!nestedModal);
        setCloseAll(true);
    }

    const initialValues = {
        avatar: "",
        fullName: "",
        phoneNumber: "",
        address: "",
        city: ""
    }

    return (
            <Formik
                initialValues={initialValues}
                onSubmit={props.onSubmit}
            >
                {formikProps => {
                    const {isSubmitting} = formikProps;
                    return (
                        <div>
                            <Button onClick={toggle} color="dark">Change Personal Information</Button>
                            <Modal
                                size="lg"
                                aria-labelledby="contained-modal-title-vcenter"
                                centered
                                isOpen={modal} toggle={toggle}
                            >
                                <ModalHeader>
                                    <Title title="Update personal information"/>
                                </ModalHeader>
                                <ModalBody>
                                    <div className="w-full xl:px-20 lg:px-16 md:px-3 sm:px-0 items-center bg-white">
                                        <div className="w-full xl:px-4 lg:px-5 md:px-1 items-center bg-white">
                                            <div className="container mx-auto">
                                                <Form className="max-w-full mx-auto bg-gray-200 p-16 rounded-md shadow-sm">
                                                    <div className="flex flex-col w-full">
                                                        <FastField
                                                            name="name"
                                                            component={InputField}
                                                            placeholder="Name"
                                                        />
                                                        <FastField
                                                            name="address"
                                                            component={InputField}
                                                            placeholder="Address"
                                                        />
                                                        <FastField
                                                            name="phoneNumber"
                                                            component={InputField}
                                                            placeholder="Phone Number"
                                                        />
                                                        <FastField
                                                            name="city"
                                                            component={SelectField}
                                                            placeholder="City"
                                                            options={CITY_OPTIONS}
                                                        />
                                                        <FormGroup>
                                                            <Button
                                                                type="submit"
                                                                className="w-full"
                                                                color="primary"
                                                                onClick={toggle}
                                                            >
                                                                {isSubmitting && <Spinner size="sm"/>}
                                                                Update
                                                            </Button>
                                                            <Modal isOpen={nestedModal} toggle={toggleNested} onClosed={closeAll ? toggle : undefined}>
                                                                <ModalHeader>Nested Modal title</ModalHeader>
                                                                <ModalBody>Stuff and things</ModalBody>
                                                                <ModalFooter>
                                                                    <Button color="primary" onClick={toggleNested}>Done</Button>{' '}
                                                                    <Button color="secondary" onClick={toggleAll}>All Done</Button>
                                                                </ModalFooter>
                                                            </Modal>
                                                        </FormGroup>
                                                    </div>
                                                </Form>
                                            </div>
                                        </div>
                                    </div>
                                </ModalBody>
                                <ModalFooter>
                                    <Button onClick={toggle}>Close</Button>
                                </ModalFooter>
                            </Modal>
                        </div>
                    )
                }}
            </Formik>
    );
}

export default UserPersonalInfo;
