import React, {useState, useEffect} from "react";
import {useDispatch, useSelector} from "react-redux";
import {updateUserInfo} from "../../../redux/actions/user.action";
import {Modal, ModalFooter, ModalBody, ModalHeader, FormGroup, Spinner} from "reactstrap"
import {Button} from "antd";
import PropTypes from "prop-types";
import * as Yup from "yup";
import {Formik, FastField, Form} from "formik";
import InputField from "../../../custom-fields/InputField";

UserPersonalInfo.defaultProps = {
    onSubmit: null,
    user: null,
}

UserPersonalInfo.propTypes = {
    onSubmit: PropTypes.func.isRequired,
    user: PropTypes.object.isRequired,
}

function UserPersonalInfo(props) {
    const dispatch = useDispatch();
    const state = useSelector((state) => state);
    const [show, setShow] = useState(false);
    const toggleShow = () => setShow(!show);
    const initialValues = {
        avatar: "",
        fullName: "",
        phoneNumber: "",
        address: "",
        city: ""
    }

    const [userInfo, setUserInfo] = useState(initialValues);
    const [image, setImage] = useState(null);

    const validationSchema = Yup.object().shape({
        name: Yup.string().required('Name is required').nullable(),
        locationTypeId: Yup.string().required('Location type is required').nullable(),
        city: Yup.string().required('City is required').nullable(),
    })

    useEffect(() => {
        setUserInfo({
            fullName: props.user && props.user.fullName ? props.user.fullName : "",
            phoneNumber: props.user && props.user.phoneNumber ? props.user.phoneNumber : "",
            address: props.user && props.user.address ? props.user.address : "",
            city: props.user && props.user.city ? props.user.city : ""
        });
    }, [props.user]); // eslint-disable-line

    const onSubmitHandler = (e) => {
        updateUserInfo(dispatch, state.auth.token, userInfo);
    };

    return (
        <div>
            <Button onClick={toggleShow} color="dark">Change Personal Information</Button>
            <Formik
                initialValues={initialValues}
                validationSchema={validationSchema}
                onSubmit={props.onSubmit}
            >
                {formikProps => {
                    const {values, setValues, isSubmitting} = formikProps;
                    return (
                        <Modal
                            size="lg"
                            aria-labelledby="contained-modal-title-vcenter"
                            centered
                            isOpen={show} toggle={toggleShow}
                        >
                            <ModalHeader closeButton>
                                <div className="text-center">
                                    <h1 className="my-3 font-semibold font-serif text-gray-800 dark:text-gray-200">Update
                                        Personal Information</h1>
                                </div>
                            </ModalHeader>
                            <ModalBody>
                                <div className="w-full xl:p-20 lg:p-16 md:p-3 sm:p-0 items-center bg-white">
                                    <div className="w-full xl:p-4 lg:p-5 md:p-1 items-center min-h-screen bg-white"
                                         data-aos="fade-up">
                                        <div className="container mx-auto">
                                            <Form className="max-w-full mx-auto bg-gray-200 p-16 rounded-md shadow-sm">
                                                <div className="flex flex-col w-full">
                                                    <FastField
                                                        name="name"
                                                        component={InputField}
                                                        placeholder="Name"
                                                    />
                                                    <div className="block w-full">
                                                        <div className="inline-block w-2/3">
                                                            <FastField
                                                                name="address"
                                                                component={InputField}
                                                                placeholder="Address"
                                                            />
                                                        </div>
                                                    </div>
                                                    <div className="block w-full">
                                                        <div className="inline-block w-1/2">
                                                            <FastField
                                                                name="contactEmail"
                                                                component={InputField}
                                                                placeholder="Email"
                                                            />
                                                        </div>
                                                        <div className="inline-block w-1/2">
                                                            <FastField
                                                                name="contactPhoneNumber"
                                                                component={InputField}
                                                                placeholder="Phone Number"
                                                            />
                                                        </div>
                                                    </div>
                                                </div>
                                            </Form>
                                        </div>
                                    </div>
                                </div>
                            </ModalBody>
                            <ModalFooter>
                                <FormGroup>
                                    <Button
                                        type="submit"
                                        className="w-full pt-1 text-white focus:outline-none"
                                        color="primary"
                                        onClick={toggleShow}
                                    >
                                        {isSubmitting && <Spinner size="sm"/>}
                                        Update
                                    </Button>
                                </FormGroup>
                                <Button onClick={toggleShow}>Close</Button>
                            </ModalFooter>
                        </Modal>
                    )
                }}
            </Formik>
        </div>

    );
}

export default UserPersonalInfo;
