import React, {useEffect, useState} from 'react';
import {Modal, ModalHeader, ModalBody, ModalFooter, Button} from "reactstrap";
import {useDispatch, useSelector} from "react-redux";
import {useHistory} from "react-router-dom";

function AddRoom() {
    const initialValues = {
        price: '',
        name: '',
        capacity: '',
        description: '',
    }
    const dispatch = useDispatch();
    const state = useSelector((state) => state);

    const [show, setShow] = useState(false);
    const [room, setRoom] = useState(initialValues)
    const toggleShow = () => setShow(!show);

    const history = useHistory();
    const onSubmitBooking = () => {
        if (state.auth.isAuthenticated) {
            setTimeout(() => {
                toggleShow();
            }, 3000)
        } else history.push("/login");
    }

    return (
        <div>
            <Button onClick={toggleShow}>Add</Button>
            <Modal isOpen={show} toggle={toggleShow} centered size="lg">
                <ModalHeader className="font-bold">
                    {name}
                </ModalHeader>
                <ModalBody className="text-2xl">
                    <p className="font-bold"><span className="font-light">Max guests: </span>{capacity > 2
                        ? `${capacity} People` : `${capacity} Person`}</p>
                    <p className="font-bold"><span className="font-light">Price: </span>{price} VND</p>
                </ModalBody>
                <ModalFooter>
                    <Button onClick={onSubmitBooking}>Book</Button>
                    <Button onClick={toggleShow}>Close</Button>
                </ModalFooter>
            </Modal>
        </div>
    );
};

export default AddRoom;
