import React, {useEffect} from 'react'
import {useDispatch, useSelector} from "react-redux";
import {setSuccess} from "../../../../redux/actions/commonActions";
import {getAdmin} from "../../../../redux/actions/admin.action";
import {Button} from "reactstrap"

function HomeAdmin() {
    const dispatch = useDispatch();
    const state = useSelector((state) => state);

    const settings = {
        autoplay: true,
        infinite: true,
        slidesToShow: 3,
        slidesToScroll: 1,
        speed: 4000,
        autoplaySpeed: 4000,
        arrows: true,
        className: "overflow-x-hidden",
        responsive: [
            {
                breakpoint: 1024,
                settings: {
                    slidesToShow: 2
                }
            },

            {
                breakpoint: 600,
                settings: {
                    slidesToShow: 1
                }
            }
        ]
    };

    useEffect(() => {
        getAdmin(dispatch, state.auth.token);
        document.title = `Profile`;
    }, []); // eslint-disable-line

    useEffect(() => {
        const timer = setTimeout(() => {
            setSuccess(dispatch, null);
        }, 3000);
        return () => clearTimeout(timer);
    }, [state.admin.success]); // eslint-disable-line

    return (
        <>
            {state.admin.user && (
                <div className="flex flex-row w-full">
                    <div className="w-4/12 md:ml-5 bg-gray-200 p-5 mx-3 rounded-sm ">
                        <div className="relative mx-auto">
                            <img
                                src={
                                    state.admin.user.image
                                        ? state.admin.user.image
                                        : "http://placehold.it/300x300?text=avatar"
                                }
                                alt="avatar"
                                className="w-32 h-32 rounded-full object-cover"
                            />
                            <h1>{state.admin.user.fullName}</h1>
                            <p>{state.admin.user.email}</p>
                        </div>
                    </div>
                    <div className="w-8/12 bg-gray-200 p-5 mx-auto rounded-sm">
                        <div className="relative mx-auto">
                            {state.admin.user.location.images.map((image, key) => {
                                return (
                                    <img
                                        src={
                                            image
                                                ? image
                                                : "http://placehold.it/300x300?text=hotel"
                                        }
                                        alt="hotel"
                                        key={key}
                                        className="w-48 h-48 object-cover"
                                    />
                                )
                            })}
                        </div>
                    </div>
                </div>
            )}
        </>
    )
}

export default HomeAdmin;
