import React, {useEffect} from 'react'
import Title from '../../Global/Title'
import Slider from "react-slick";
import { useDispatch, useSelector } from 'react-redux'
import { getCities } from '../../../redux/actions/city.action'

function Cities() {
    const dispatch = useDispatch();
    const state = useSelector((state) => state);

    useEffect(() => {
        getCities(dispatch);
    },[dispatch]);

    return (
        <> 
            <Title title="The most attractive destinations in Vietnam" />
            <div className="flex mx-auto px-8 py-5" data-aos="fade-left">
                <Slider {...settings}>
                    {state.city && state.city.cities.map((city) => {
                        return(
                            <div className="w-full mx-auto cursor-pointer px-4" key={city.id}>
                                <div className="w-48 h-48 flex items-center justify-center">
                                    <img src={city.thumbnail} alt="city" className="w-44 h-44 object-cover rounded-full hover:shadow-2xl"/>
                                </div>
                                <p className="mx-12 text-lg font-semibold font-serif">
                                    {city.name}
                                </p>
                                <span>{city.averagePrice}</span>
                            </div>
                        )
                    })}
                </Slider>
            </div>
        </>
    )
}

export default Cities;
