import React from 'react';
import PropTypes from 'prop-types'
import {Formik, Form, FastField} from 'formik'
import * as Yup from 'yup'

import SelectField from '../../custom-fields/SelectField'
import {GUEST_OPTIONS , CITY_OPTIONS} from '../../constants/global'


Searchbar.propTypes = {
    onSubmit: PropTypes.func,
}

Searchbar.defaultProps = {
    onSubmit: null,
}

function Searchbar(props) {
    const initSearchData = {
        check_in: "",
        check_out: "",
        city: "",
        guest: 1
    };

    return (
                <Formik
                    initialValues={initSearchData}
                    onSubmit={props.onSubmit}
                >
                    {formikProps => {
                        const {values, errors, touched, isSubmitting} = formikProps;
                        console.log({values, errors, touched});

                        return (
                            <Form>
                                <div className="flex flex-col md:mr-3 w-full md:w-3/4 ">
                                    <label htmlFor='check-in' className="text-gray-900 text-xl">
                                        Check-in
                                    </label>
                                    <input
                                        id= 'check-in'
                                        className="px-6 py-3 mt-2 w-full"
                                        type="date"
                                        value={initSearchData.check_in}
                                        onChange={(e) =>
                                            setInitSearchData({
                                                ...initSearchData,
                                                check_in: e.target.value
                                            })
                                        }
                                    />
                                </div>
                                <div className="flex flex-col md:mr-3 w-full md:w-3/4 ">
                                    <label htmlFor='check-out' className="text-gray-900 text-xl">
                                        Check-out
                                    </label>
                                    <input
                                        id='check-out'
                                        className="px-6 py-3 mt-2 w-full"
                                        type="date"
                                        value={initSearchData.check_out}
                                        onChange={(e) =>
                                            setInitSearchData({
                                                ...initSearchData,
                                                check_out: e.target.value
                                            })
                                        }
                                    />
                                </div>
                                <FastField 
                                    name="city"
                                    component={SelectField}

                                    label="City"
                                    placeholder="What do you wanna go?"
                                    options={CITY_OPTIONS}
                                />   

                                <FastField 
                                    name="guest"
                                    component={SelectField}

                                    label="Guest"
                                    placeholder="Guest number?"
                                    options={GUEST_OPTIONS}
                                /> 
                            </Form>
                        )          
                   }
                }      
            </Formik>
    );
}

export default Searchbar;
