import React, {useState, useEffect} from "react";
import {useDispatch, useSelector} from "react-redux";
import {Modal, ModalFooter, ModalBody, ModalHeader, FormGroup, Spinner, Button} from "reactstrap"
import PropTypes from "prop-types";
import {Formik, FastField, Form} from "formik";
import InputField from "../../../custom-fields/InputField";
import Title from "../../Global/Title";
import SelectField from "../../../custom-fields/SelectField";
import {CITY_OPTIONS} from "../../../constants/global";

UserPersonalInfo.defaultProps = {
    onSubmit: null,
    user: null,
}

UserPersonalInfo.propTypes = {
    onSubmit: PropTypes.func.isRequired,
    user: PropTypes.object.isRequired,
}

function UserPersonalInfo(props) {
    const [modal, setModal] = useState(false);
    const [nestedModal, setNestedModal] = useState(false);
    const [closeAll, setCloseAll] = useState(false);

    const toggle = () => setModal(!modal);
    const toggleNested = () => {
        setNestedModal(!nestedModal);
        setCloseAll(false);
    }
    const toggleAll = () => {
        setNestedModal(!nestedModal);
        setCloseAll(true);
    }

    const initialValues = {
        avatar: "",
        fullName: "",
        phoneNumber: "",
        address: "",
        city: ""
    }

    return (
        <div>
            <Button onClick={toggleShow} color="dark">Change Personal Information</Button>
            <Formik
                initialValues={initialValues}
                onSubmit={props.onSubmit}
            >
                {formikProps => {
                    const {isSubmitting} = formikProps;
                    return (
                        <Modal
