                        >
                            <ModalHeader closeButton>
                                <Title title="Update personal information"/>
                            </ModalHeader>
                            <ModalBody>
                                <div className="w-full xl:px-20 lg:px-16 md:px-3 sm:px-0 items-center bg-white">
                                    <div className="w-full xl:px-4 lg:px-5 md:px-1 items-center bg-white"
                                         data-aos="fade-up">
                                        <div className="container mx-auto">
                                            <Form inline className="max-w-full mx-auto bg-gray-200 p-16 rounded-md shadow-sm">
                                                <div className="flex flex-col w-full">
                                                    <FastField
                                                        name="name"
                                                        component={InputField}
                                                        placeholder="Name"
                                                    />
                                                    <FastField
                                                        name="address"
                                                        component={InputField}
                                                        placeholder="Address"
                                                    />
                                                    <FastField
                                                        name="phoneNumber"
                                                        component={InputField}
                                                        placeholder="Phone Number"
                                                    />
                                                    <FastField
                                                        name="city"
                                                        component={SelectField}
                                                        placeholder="City"
                                                        options={CITY_OPTIONS}
                                                    />
                                                    <FormGroup>
                                                        <Button
                                                            type="submit"
                                                            className="w-full"
                                                            color="primary"
                                                            onClick={toggleShow}
                                                        >
                                                            {isSubmitting && <Spinner size="sm"/>}
                                                            Update
                                                        </Button>
                                                        <Modal isOpen={nestedModal} toggle={toggleNested} onClosed={closeAll ? toggle : undefined}>
                                                            <ModalHeader>Nested Modal title</ModalHeader>
                                                            <ModalBody>Stuff and things</ModalBody>
                                                            <ModalFooter>
                                                                <Button color="primary" onClick={toggleNested}>Done</Button>{' '}
                                                                <Button color="secondary" onClick={toggleAll}>All Done</Button>
                                                            </ModalFooter>
                                                        </Modal>
                                                    </FormGroup>
                                                </div>
                                            </Form>
                                        </div>
                                    </div>
                                </div>
                            </ModalBody>
                            <ModalFooter>
                                <Button onClick={toggleShow}>Close</Button>
                            </ModalFooter>
                        </Modal>
                    )
                }}
            </Formik>
        </div>

    );
}

export default UserPersonalInfo;
