import React from "react";
import {Link} from "react-router-dom";
import {Badge} from "reactstrap"


function HotelCard({hotel: {id, name, price, score}}) {
    const formattedPrice = price => {
        return price !== null && price.slice(1)
    }
    return (
        <div className="p-0 lg:p-1">
            <div
                className="bg-gray-100 h-20 xl:h-20 rounded-xl overflow-hidden shadow-lg mt-0"
                data-aos="fade-up"
            >
                <Link to={`/super-admin/create-owner/${id}`} className="no-underline hover:no-underline">
                    <div className="relative">
                        <div className="flex items-center justify-between w-full absolute bottom-0">
                            <div className="absolute w-full h-full bg-gray-800 opacity-50"/>
                            <div className="flex items-center justify-between p-2 w-full z-10 h-12">
                                <div className="flex items-center justify-between">
                                    <h3>
                                        <Badge color="primary">{score}</Badge>
                                    </h3>
                                </div>
                                <div className="text-gray-100 font-semibold">
                                    Around {formattedPrice(price)} VND
                                </div>
                            </div>
                        </div>
                    </div>

                    <p className="p-4 text-lg uppercase font-bold font-sans-roboto text-center text-gray-800 hover:text-gray-600">
                        {name}
                    </p>
                </Link>
            </div>
        </div>
    );
}

export default HotelCard;
