import React, {useEffect} from 'react'
import Title from '../../Global/Title'
import Slider from "react-slick";
import { useDispatch, useSelector } from 'react-redux'
import { getCities } from '../../../redux/actions/city.action'

function Citys() {
    const dispatch = useDispatch();
    const state = useSelector((state) => state);

    useEffect(() => {
        getCities(dispatch);
    },[dispatch]);

    const citys = state.city.cities;

    var settings = {
        autoplay: true,
        infinite: true,
        slidesToShow: 6,
        slidesToScroll: 2,
        speed: 2000,
        autoplaySpeed: 2000,
        className: "overflow-x-hidden",
        responsive: [
            {
                breakpoint: 1400,
                settings: {
                    slidesToShow: 4
                }
            },
            {
                breakpoint: 1024,
                settings: {
                    slidesToShow: 3
                }
            },

            {
                breakpoint: 800,
                settings: {
                    slidesToShow: 2
                }
            }
        ]
    };

    return (
        <> 
            <Title title="The most attractive destinations in Vietnam" />
            <div className="flex mx-auto px-8 py-5" data-aos="fade-left">
                <Slider {...settings}>
                    {state.citys && citys.map((city) => {
                        return(
                            <div className="w-full mx-auto cursor-pointer px-4" key={city.id}>
                                <div className="w-48 h-48 flex items-center justify-center">
                                    <img src={city.image} alt="city" className="w-44 h-44 object-cover rounded-full hover:shadow-2xl"/>
                                </div>
                                <p className="mx-12 text-lg font-semibold font-serif">
                                    {city.label}
                                </p>
                            </div>
                        )
                    })}
                </Slider>
            </div>
        </>
    )
}

export default Citys;
