/* eslint-disable react-hooks/exhaustive-deps */
import React, {useEffect, useState} from 'react'
import {useDispatch, useSelector} from "react-redux";
import {Formik, Form, FastField} from 'formik'
import {Button, FormGroup, Spinner} from 'reactstrap'
import PropTypes from 'prop-types'
import * as Yup from 'yup'

import InputField from '../../../../custom-fields/InputField'
import {useSecureLs} from "../../../Global/UseSecureLs";

CreateOwnerForm.defaultProps = {
    onSubmit: null,
}

CreateOwnerForm.propTypes = {
    onSubmit: PropTypes.func,
}

export default function CreateOwnerForm(props) {
    const dispatch = useDispatch();
    const state = useSelector((state) => state);

    const [admin_token, setAdminToken] = useSecureLs("admin_token");
    const [admin_id, setAdminId] = useSecureLs("admin_id");

    const initialValues = {
        fullName: "",
        email: "",
        password: "",
        locationId: null,
    }

    useEffect(() => {
        document.title = "Create an owner for location";
    }, [])

    const validationSchema = Yup.object().shape({
        email: Yup.string().required('Email is required').nullable(),
        locationTypeId: Yup.string().required('Location type is required').nullable(),
        city: Yup.string().required('City is required').nullable(),
    })

    return (
        <Formik
            initialValues={initialValues}
            validationSchema={validationSchema}
            onSubmit={props.onSubmit}
        >
            {formikProps => {
                const {values, setValues, isSubmitting} = formikProps;
                return (
                    <div className="w-full xl:p-20 lg:p-16 md:p-3 sm:p-0 items-center min-h-screen bg-white">
                        <div className="text-center">
                            <h1 className="my-3 font-semibold font-serif text-gray-800 dark:text-gray-200">Create
                                new Location</h1>
                        </div>
                        <div className="w-full xl:p-4 lg:p-5 md:p-1 items-center min-h-screen bg-white"
                             data-aos="fade-up">
                            <div className="container mx-auto">
                                <Form className="max-w-full mx-auto bg-gray-200 p-16 rounded-md shadow-sm">
                                    <div className="flex flex-col w-full">
                                        <FastField
                                            name="name"
                                            component={InputField}
                                            placeholder="Name"
                                        />
                                        <div className="block w-full">
                                            <div className="inline-block w-1/2">
                                                <FastField
                                                    name="contactEmail"
                                                    component={InputField}
                                                    placeholder="Email"
                                                />
                                            </div>
                                            <div className="inline-block w-1/2">
                                                <FastField
                                                    name="contactPhoneNumber"
                                                    component={InputField}
                                                    placeholder="Phone Number"
                                                />
                                            </div>
                                        </div>
                                        <FastField
                                            name="price"
                                            component={InputField}
                                            placeholder="Average Price"
                                        />
                                        <FastField
                                            name="description"
                                            component={InputField}
                                            placeholder="Description"
                                        />
                                        <FormGroup>
                                            <Button
                                                type="submit"
                                                className="w-full pt-1 text-white focus:outline-none"
                                                onClick={() => {
                                                    setValues({
                                                        ...values,
                                                        locationTypeId: state.city.locationTypes[values.locationTypeId - 1].id,
                                                    });
                                                }}
                                            >
                                                {isSubmitting && <Spinner size="sm"/>}
                                                Create
                                            </Button>
                                        </FormGroup>
                                    </div>

                                </Form>
                            </div>
                        </div>
                    </div>
                )
            }}
        </Formik>
    )
}
