/* eslint-disable no-sequences */
import React, { useEffect } from "react";
import Slider from "react-slick";
import { useSelector, useDispatch } from "react-redux"
import { getHotelImages } from "../../../../redux/actions/hotel.action"

import "react-image-lightbox/style.css";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";


function HotelSlider() {
    const dispatch = useDispatch();
    const state = useSelector((state) => state);

    useEffect(() => {
        getHotelImages(dispatch);
    }, []); // eslint-disable-line
    const images = state.hotel.images;

    var settings = {
        draggable: true,
        infinite: true,
        slidesToShow: 1,
        slidesToScroll: 1,
        autoplay: true,
        speed: 4000,
        autoplaySpeed: 4000,
        className: "overflow-x-hidden shadow-xl",
        responsive: [
            {
                breakpoint: 600,
                settings: {
                    slidesToShow: 1
                }
            },
            {
                breakpoint: 480,
                settings: {
                    slidesToShow: 1
                }
            }
        ]
    };
    return (
        <>
            {images !== null && (
                <div className="m-0 p-0 z-0 opacity-75" data-aos="fade-up">
                    <div className="m-0 md:mt-0 relative p-0">
                        <Slider {...settings}>
                            {images.map((element, i) => (
                                <img
                                    src={element}
                                    key={i}
                                    alt="slider"
                                    className="h-screen w-full object-cover"
                                />
                            ))}
                        </Slider>
                    </div>
                </div>
            )
            }
        </>
    )
}

export default HotelSlider
