import React, {useEffect} from 'react'
import Title from '../../Global/Title'
import {useDispatch, useSelector} from 'react-redux'
import {getCities} from '../../../redux/actions/city.action'
import {Link} from 'react-router-dom'
import Slider from "react-slick";
import {Badge} from 'reactstrap'


function Cities() {
    const dispatch = useDispatch();
    const state = useSelector((state) => state);

    useEffect(() => {
        getCities(dispatch);
    }, []);

    const settings = {
        autoplay: true,
        infinite: true,
        slidesToShow: 3,
        slidesToScroll: 1,
        speed: 4000,
        autoplaySpeed: 4000,
        arrows: true,
        className: "overflow-x-hidden",
        responsive: [
            {
                breakpoint: 1024,
                settings: {
                    slidesToShow: 2
                }
            },

            {
                breakpoint: 600,
                settings: {
                    slidesToShow: 1
                }
            }
        ]
    };

    return (
        <>
            <Title title="The most attractive destinations in Vietnam"/>
            <div className="flex mx-auto px-8 py-5" data-aos="fade-up">
                <Slider {...settings}>
                    {state.city && state.city.cities.map((city) => {
                        return (
                            <Link to={`/city/${city.id}`} key={city.id}
                                  className="no-underline hover:no-underline text-black hover:text-gray-700 px-8 mx-auto">
                                <div className="w-60 h-60 flex items-center justify-center">
                                    <img src={city.thumbnail} alt="city"
                                         className="w-56 h-56 object-cover rounded-full hover:shadow-2xl"/>
                                </div>
                                <p className="mx-16 mt-3 text-lg font-semibold font-serif">
                                    {city.name}
                                </p>
                            </Link>
                        )
                    })}
                </Slider>

            </div>
        </>
    )
}

export default Cities;
