import React, { useState } from 'react'
import PropTypes from 'prop-types'
import { Formik, Form, FastField } from 'formik'
import { Button, FormGroup, Spinner } from 'reactstrap'
import * as Yup from 'yup'

import SelectField from '../../../../custom-fields/SelectField'
import { CITY_OPTIONS } from '../../../../constants/global'

CreateLocation.propTypes = {
    onSubmit: PropTypes.func,
}

CreateLocation.defaultProps = {
    onSubmit: null,
}

export default function CreateLocation(props) {
    const initialValues = {
        locationTypeId: '',
        name: '',
        address: '',
        city: '',
        workingTime: '',
        contactPhoneNumber: '',
        contactEmail: '',
        price: '',
        description: '',
        thumbnail: '',
        images: [],
        isFeatured: false,
    }

    const validationSchema = Yup.object().shape({
        name: Yup.string().required('Name is required').nullable(),
        locationTypeId: Yup.string().required('Location type is required').nullable(),
        city: Yup.string().required('City is required').nullable(),
    })

    return (
        <Formik
            initialValues={initialValues}
            validationSchema={validationSchema}
            onSubmit={props.onSubmit}
        >
            {formikProps => {
                const { isSubmitting } = formikProps;

                return (
                    <div ref={props.refProp}>
                        <div className="items-center min-h-0 bg-white" data-aos="fade-up">
                            <div className="container mx-auto">
                                <Form className="w-full mx-auto my-5 xl:my-20 bg-gray-200 p-8 rounded-md shadow-sm">
                                    <div className="text-center">
                                        <h1 className="my-3 text-xl font-semibold text-gray-800 dark:text-gray-200">Create new Location</h1>
                                    </div>
                                    <div className="block w-full">
                                        <div className="inline-block w-1/4">

                                        </div>
                                        <div className="inline-block w-1/4">
                                            <FastField
                                                name="cityId"
                                                component={SelectField}
                                                placeholder="City"
                                                options={CITY_OPTIONS}
                                            />
                                        </div>
                                        <div className="inline-block w-1/4">

                                        </div>
                                        <FormGroup
                                            className="w-1/4 inline-block"
                                        >
                                            {/* <Link
                                                className="no-underline text-white"
                                                to={`/search/from_to=${initialValues.fromTo}
                                                &city=${initialValues.cityId}
                                                &guest=${initialValues.guest}`}
                                            > */}
                                            <Button
                                                type="submit"
                                                className="w-full text-white focus:outline-none"
                                            >
                                                {isSubmitting && <Spinner size="sm" />}
                                                Find
                                            </Button>
                                            {/* </Link> */}
                                        </FormGroup>
                                    </div>

                                </Form>
                            </div>
                        </div>
                    </div>
                )
            }}
        </Formik>
    )
}
