import React, { useEffect, useState } from "react";
import Footer from "../../components/Footer";
import Border from "../../components/Border";
import Title from "../../components/Title";

import HotelHero from "./HotelHero";

import { getHotel } from "../../redux/actions/hotels";
import { useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { useHistory } from "react-router-dom";
import { useSecureLs } from "../../components/UseSecureLs";

function Hotel(props) {
    const state = useSelector((state) => state);
    let { id } = useParams();
    const [_user_id] = useSecureLs("user_id");
    const [userId, setUserId] = useState(_user_id);

    useEffect(() => {
        getHotel(dispatch, id);
    }); // eslint-disable-line

    useEffect(() => {
        setUserId(userId);
    }, []); // eslint-disable-line

    let history = useHistory();
    useEffect(() => {
        const timer = setTimeout(() => {
            if (!state.hotel.hotels) {
                history.push({
                    pathname: "/404",
                    state: {
                        message:
                            "There is no such hotel, you'll be redirected in a bit"
                    }
                });
            }
        }, 3000);
        document.title = `${state.hotel.hotels.name} Hotel`;
        return () => clearTimeout(timer);
    }, [state.hotel.hotels]); // eslint-disable-line
    return (
        <>
            <HotelHero hotel={state.hotel.hotels} />

            {state.hotel.hotels && (
                <Title title={`${state.hotel.hotels.name}'s Rooms`} />
            )}

            <Border my="16" />

            {state.hotel.hotels && (
                <Title
                    title={`Where to find ${state.hotel.hotels.name} Hotel`}
                />
            )}

            <Border />

            <Footer />
        </>
    );
}

export default Hotel;
