/* eslint-disable react-hooks/exhaustive-deps */
import React, {useEffect, useState} from "react";
import {getPageHotels} from "../../../../redux/actions/hotel.action";
import {useSelector, useDispatch} from "react-redux";
import Pagination from "../../../Global/Pagination";
import Title from "../../../Global/Title"
import {Table} from "reactstrap";
import {formatDate} from "../../../../constants/function";

function Hotels() {
    const dispatch = useDispatch();
    const state = useSelector((state) => state);
    const [pagination, setPagination] = useState({
        page: 2,
        count: 10,
        total: 606,
    });

    const handlePageChange = (newPage) => {
        setPagination({
            ...pagination,
            page: newPage
        });
    }

    useEffect(() => {
        const timer = setTimeout(() => {
            getPageHotels(dispatch, pagination.page)
        }, 500);

        return () => clearTimeout(timer);
    }, [pagination]);


    return (
        <section className="px-4 py-12 md:px-10 lg:px-16">
            <Title title="Hotels"/>
            <div className="sm:mx-0 md:mx-10 lg:mx-20 xl:mx-40">
                <Table striped bordered hover>
                    <thead>
                    <tr>
                        <th>ID</th>
                        <th>Name</th>
                        <th>Room</th>
                        <th>Check-in</th>
                        <th>Check-out</th>
                        <th>Status</th>
                    </tr>
                    </thead>
                    <tbody>
                    {state.user.bookings && state.user.bookings.map((booking, key) => {
                        return (
                            <tr key={key}>
                                <td className="grid grid-cols-5">
                                    {key + 1}
                                </td>
                                <th>{formatDate(booking.createdAt)}</th>
                                <td>{booking.location.name}</td>
                                <td>{booking.room.name}</td>
                                <td>{formatDate(booking.startTime)}</td>
                                <td>{formatDate(booking.endTime)}</td>
                                <td>{booking.status}</td>
                            </tr>
                        )
                    })}
                    </tbody>
                </Table>
            </div>
            <Pagination
                pagination={pagination}
                onPageChange={handlePageChange}
            />
        </section>
    );
}

export default Hotels;
