import React, {useState} from 'react';
import {Modal, ModalHeader, ModalBody, ModalFooter, Button} from "reactstrap";

function BookingModal({room: {id, name, price, capacity}}) {
    const formatPrice = (price) => {
        let priceString = '';
        price = Math.floor(price);
        while (price > 999) {
            let num = price % 1000;
            priceString += '.' + num;
            price = Math.floor(price / 1000);
            if (price <= 999) {
                priceString = price + '' + priceString;
                break;
            }
        }
        return priceString;
    }

    const [show, setShow] = useState(false);
    const toggleShow = () => setShow(!show);
    const book = () => {
        alert("Your request is pending.")
    }
    return (
        <div>
            <Button onClick={toggleShow}>Book</Button>
            <Modal isOpen={show} toggle={toggleShow} centered size="lg">
                <ModalHeader className="font-bold">
                    {name}
                </ModalHeader>
                <ModalBody className="text-xl items-between">
                    <p className="font-bold"><span className="font-thin">Max guests: </span>{capacity > 2
                        ? `${capacity} People` : `${capacity} Person`}</p>
                    <p className="font-bold"><span className="font-thin">Price: </span>{formatPrice(price)} VND</p>
                </ModalBody>
                <ModalFooter>
                    <Button onClick={book}>Book</Button>
                    <Button onClick={toggleShow}>Close</Button>
                </ModalFooter>
            </Modal>
        </div>
    );
};

export default BookingModal;
